import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";

function ExpenseItem(props) {
  //   const expenseDate = new Date(2021, 6, 13);  ==> this was a way to use it instead of passing props and getting the value from outside. Better to save value in a variable then in the jscode ,Easier to read
  //   const expenseTitle = "Car Insurance";
  //   const expenseAmmount = 295.57;

  const clickHandler = () => {
    console.log("Clicked!!");
  };

  return (
    <div className="expense-item">
      <ExpenseDate date={props.date} />
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
      <button onClick={clickHandler}>Change Title</button>
    </div>
  );
}

export default ExpenseItem;
